INSERT INTO `business` (`id`, `city_id`, `name`, `latitude`, `longitude`, `logo`, `google_page_url`, `rating`, `address`, `email`, `phone`, `reviews`, `favourite`, `description`, `program`, `opening_hour`, `closing_hour`) VALUES
(1, 2, 'Hotel Mandachi', 47.6217, 26.2400, '../css/images/hotel_mandachi.jpg', 'https://hotelmandachi.com/en/', 4.7, 'Bulevardul Sofia Vicoveanca 107, Suceava, Suceava, România', 'office@hotelmandachi.ro', '+40 330 111 999 / +40 753 169 300', 'https://www.google.com/travel/search?gsas=1&ts=EggKAggDCgIIAxocEhoSFAoHCOgPEAwYCxIHCOgPEAwYDBgBMgIQAA&qs=MhNDZ29JaGZTcWdzZVI1OEJ6RUFFOAI&ap=ugEHcmV2aWV3cw&hl=en-RO&ved=0CAAQ5JsGahcKEwigv4v8vM6IAxUAAAAAHQAAAAAQDQ', 1, 'At Mandachi Hotel & Spa, we welcome you with a warm and friendly atmosphere, where minimalist design with traditional influences, carefully thought-out decorations, and spacious rooms harmoniously blend to offer you an unforgettable experience. We pride ourselves on our top-notch services, our ultra-modern Spa & Wellness complex, and premium facilities for events and conferences, all designed to ensure your comfort and relaxation.\nLocated in the heart of Bucovina, close to the main tourist attractions, Mandachi Hotel & Spa is a unique example of 4-star hospitality, everything being developed with care and passion for our guests. Over time, we have shaped a philosophy of hospitality that aims to satisfy even the most demanding expectations, ensuring that each visit will be a memorable experience.', 'Monday: 12:00 - 22:00 \nTuesday: 12:00 - 22:00 \nWednesday: 12:00 - 22:00 \nThursday: 12:00 - 22:00 \nFriday: 12:00 - 22:00 \nSaturday: 12:00 - 22:00 \nSunday: 12:00 - 22:00', '12:00', '22:00'),
(2, 2, 'Hotel Continental', 47.6461, 26.2554, '../css/images/continental_logo.jpg', 'https://mycontinental-suceava.continentalhotels.ro/', 4.1, 'Strada Mihai Viteazul nr. 4, cod 720057, Suceava, România', 'suceava@continentalhotels.ro', '+40 372 304 904', 'https://www.google.com/travel/search?gsas=1&ts=EggKAggDCgIIAxocEhoSFAoHCOgPEAwYAhIHCOgPEAwYAxgBMgIQAA&qs=MhNDZ29JMkxUMmtyT0UxWmxHRUFFOAI&ap=ugEHcmV2aWV3cw&hl=en-RO&ved=0CAAQ5JsGahcKEwjYqdq6vc6IAxUAAAAAHQAAAAAQDQ', 0, 'MyContinental Suceava is a hotel in Suceava that offers accommodation services in the city center. This 3-star hotel is centrally located and is in immediate proximity to the main tourist attractions. Additionally, the MyContinental Suceava hotel is near the city business objectives. In 1970, the hotel building received the Architects Union award and was featured in specialized literature.\nHotel MyContinental Suceava is a 3-star hotel, renovated in 2016. Surrounding our hotel are the most important administrative points such as banks, cultural centers, museums, commercial points of interest, as well as the headquarters of the main companies in the city.\nThe hotel is located 3.3 km from the largest shopping center in the city (Iulius Mall Suceava) and 12 km from the \"Ștefan cel Mare\" International Airport.', 'Monday: 12:00 - 22:30 \nTuesday: 12:00 - 22:30 \nWednesday: 12:00 - 22:30 \nThursday: 12:00 - 22:30 \nFriday: 12:00 - 22:30 \nSaturday: 12:00 - 22:30 \nSunday: 12:00 - 22:30', '12:00', '22:30'),
(3, 2, 'Hotel Balada', 47.6403, 26.2632, '../css/images/hotel_balada.jpg', 'https://hotelbalada.ro/', 4.6, 'Strada Mitropoliei, Numărul 5 720035, Suceava, România', 'contact@hotelbalada.ro', '+40 330 100 026', 'https://www.google.com/travel/search?gsas=1&ts=EggKAggDCgIIAxocEhoSFAoHCOgPEAkYGBIHCOgPEAkYGRgBMgIQAA&qs=MhRDZ3NJb3FEOHBKM3ktZjZKQVJBQjgC&ap=ugEHcmV2aWV3cw&hl=en-RO&ved=0CAAQ5JsGahcKEwjYg5nGvc6IAxUAAAAAHQAAAAAQDQ', 0, 'BALADA HOTEL is located in the center of Suceava city, just a few steps from the Saint John the New Monastery and only a ten-minute walk from the Suceava Fortress.\nBALADA is a modern hotel that offers free high-speed Wi-Fi access and a restaurant with Romanian and international dishes. The rooms are decorated in warm colors and are equipped with a minibar and a flat-screen TV.\nThe rooms feature a desk, a safe, and a private bathroom with a bathtub, complete with toiletries. With us, you can enjoy a rich buffet breakfast every morning, including vegetarian options.\nThe restaurant offers a diversified menu, fine desserts, and a wide range of superior quality wines. The hotel is located 29 km from Gura Humorului. Botoşani is 33 km from BALADA HOTEL, and Suceviţa is 44 km away.', '-', '-', '-'),
(4, 2, 'Restaurant Ceaunul Bunicii', 47.6428, 26.2453, '../css/images/ceaunul_bunicii.png', 'https://www.ceaunulbunicii.ro/', 4.4, 'Strada Universității 42B, Suceava', 'contact@ceaunulbunicii.ro', '+40 749 430 418 / +40 771 066 540', 'https://www.google.com/search?sca_esv=658059ba84023065&sca_upv=1&tbm=lcl&sxsrf=ADLYWILsh1__MEPMHiPWa43WUnVVH4hgYw:1726730829454&q=Ceaunul+Bunicii+Reviews&rflfq=1&num=20&stick=H4sIAAAAAAAAAONgkxIxNDQ1tDCwMDU3MzUyNrA0MjQ0Md7AyPiKUdw5NbE0rzRHwak0LzM5M1MhKLUsM7W8eBErLhkA6UVUG08AAAA&rldimm=11518085765230921143&hl=en-RO&sa=X&ved=2ahUKEwjqgdnNvc6IAxXuSfEDHWbqAnwQ9fQKegQIOxAF&biw=1920&bih=919&dpr=1#lkt=LocalPoiReviews', 0, 'We bring traditional Romanian recipes passed down from generation to generation to your tables. Browse the online menu with the option to order for pickup from the restaurant or have it delivered right to your doorstep!', 'Monday: 08:00 - 20:00 \nTuesday: 08:00 - 20:00 \nWednesday: 08:00 - 20:00 \nThursday: 08:00 - 20:00 \nFriday: 08:00 - 20:00 \nSaturday: 08:00 - 20:00 \nSunday: closed', '08:00', '20:00'),
(5, 2, 'Restaurant Casa Bucovineana', 47.6506, 26.2548, '../css/images/casa-bucovineana-logo.png', 'https://casabucovineana.ro/', 4.5, 'Strada Petru Rareș 57B, Suceava 727525', 'casabucovineana@gmail.com', '0746 908 070 / 0744 211 833', 'https://www.google.com/travel/search?gsas=1&ts=EggKAggDCgIIAxocEhoSFAoHCOgPEAkYFBIHCOgPEAkYFRgBMgIQAA&qs=MhRDZ3NJc0tuWG0tN0I1djZtQVJBQjgC&ap=ugEHcmV2aWV3cw&hl=en-RO&ved=0CAAQ5JsGahcKEwjow6zdvc6IAxUAAAAAHQAAAAAQDQ', 0, 'Located in the central area of Suceava, just 5 minutes from the city center on a quiet street away from the city noise, our restaurant offers a traditional lounge (smoking, 60 seats), a classic lounge (non-smoking, 30 seats), and a terrace/summer garden (30 seats). The venue has ample parking spaces, including coach access.\nThe traditional lounge, with its unique architecture, showcases the interior of a traditional household courtyard from the Bucovina region. While enjoying your meal, you can admire a traditional Bucovina house facade, a well from the Moldovița area, and a traditional Bucovinian gate. All these elements are almost life-sized, adapted only to fit within the traditional lounge. The Classic Lounge comprises three rooms in a house from 1933, furnished in a classic style. Please note that the house original interior doors have been restored and preserved. The location also features a summer garden with a capacity of 30 seats, where you can spend a peaceful afternoon dining with friends or family.', 'Monday: 07:00 - 22:00 \nTuesday: 07:00 - 22:00 \nWednesday: 07:00 - 22:00 \nThursday: 07:00 - 22:00 \nFriday: 07:00 - 22:00 \nSaturday: 07:00 - 22:00 \nSunday: 07:00 - 22:00', '07:00', '22:00'),
(6, 2, 'Taco Loco', 47.6423, 26.2573, '../css/images/taco-loco-logo.png', 'https://www.tacolocosv.ro/', 4.1, 'Str. Vasile Bumbac 5A, Suceava', 'office@tacolocosv.ro', '0230 220 032 / 0748 220 032', 'https://www.google.com/search?sca_esv=658059ba84023065&sca_upv=1&tbm=lcl&sxsrf=ADLYWIKyw-NFPYohgVGnMPdD-2OuFd_fnA:1726730878204&q=Taco+Loco+Reviews&rflfq=1&num=20&stick=H4sIAAAAAAAAAONgkxIxNDE1NbAwNjOzNDE3sTA2MjSwNN7AyPiKUTAkMTlfwScfSASllmWmlhcvYsUUAwD5UqLrQwAAAA&rldimm=14550836694748321093&hl=en-RO&sa=X&ved=2ahUKEwiH1Pjkvc6IAxUI3AIHHekZG8EQ9fQKegQIWBAF&biw=1920&bih=919&dpr=1#lkt=LocalPoiReviews', 0, 'Restaurant Taco Loco is located in the center of Suceava, next to the Finance Palace. Even though for many, the city center can mean noise and bustle, in reality, this location enjoys the most peaceful atmosphere, complemented by high-quality services and delicious food.', 'Monday: 09:00 - 23:00 \nTuesday: 09:00 - 23:00 \nWednesday: 09:00 - 23:00 \nThursday: 09:00 - 23:00 \nFriday: 09:00 - 23:00 \nSaturday: 09:00 - 23:00 \nSunday: 09:00 - 23:00', '09:00', '23:00'),
(7, 2, 'Restaurant Union', 47.6444, 26.2611, '../css/images/union-logo.jpg', 'https://www.restaurant-union.ro/', 4.2, 'Str. Ana Ipatescu Nr. 7, 720026 Suceava', '-', '0737 001 913', 'https://www.google.com/search?sca_esv=658059ba84023065&sca_upv=1&tbm=lcl&sxsrf=ADLYWIII25U8RNmU-f8Zm3hD50o3XZJ_Dw:1726730913244&q=Union+Reviews&rflfq=1&num=20&stick=H4sIAAAAAAAAAONgkxIxNDM3tDAxMjM2MDC3NDUyNTAxM9nAyPiKkTc0LzM_TyEotSwztbx4ESsqHwAr3wrqOwAAAA&rldimm=16718426300795250464&hl=en-RO&sa=X&ved=2ahUKEwj6ndP1vc6IAxUNS_EDHZTDDrYQ9fQKegQIPRAF&biw=1920&bih=919&dpr=1#lkt=LocalPoiReviews', 0, 'The Union Restaurant is located in the center of Suceava, near the city main attractions. The restaurant offers a varied menu, with traditional Romanian dishes, as well as international cuisine. The restaurant has a capacity of 100 seats, and the terrace has 50 seats. The restaurant is suitable for organizing various events, such as weddings, baptisms, anniversaries, and corporate events.', 'Monday: 08:00 - 22:00 \nTuesday: 08:00 - 22:00 \nWednesday: 08:00 - 22:00 \nThursday: 08:00 - 22:00 \nFriday: 08:00 - 22:00 \nSaturday: 08:00 - 22:00 \nSunday: 08:00 - 22:00', '08:00', '22:00');
